document.addEventListener("DOMContentLoaded",function(){let elements=document.querySelectorAll("[data-toggle-on],[data-on],[data-off],[data-show],[data-hide],[data-toggle],[data-slide],[data-remove],[data-action],[data-click],[data-href]");for(let i=0;i<elements.length;i++){elements[i].addEventListener("click",function(a){let el=a.target;el.getAttribute("data-toggle-on")!==null?document.querySelector(el.getAttribute("data-toggle-on")).classList.toggle("on"):"";if(el.getAttribute("data-toggle")!==null){let targets=document.querySelectorAll(el.getAttribute("data-toggle"));for(let x=0;x<targets.length;x++){targets[x].style.display=(targets[x].style.display==="none"?"block":"none")}}if(el.getAttribute("data-hide")!==null){let targets=document.querySelectorAll(el.getAttribute("data-hide"));for(let x=0;x<targets.length;x++){targets[x].style.display="none"}}if(el.getAttribute("data-show")!==null){let targets=document.querySelectorAll(el.getAttribute("data-show"));for(let x=0;x<targets.length;x++){targets[x].style.display="block"}}if(el.getAttribute("data-click")!==null){let targets=document.querySelectorAll(el.getAttribute("data-click"));for(let x=0;x<targets.length;x++){targets[x].click()}}if(el.getAttribute("data-remove")!==null){let targets=document.querySelectorAll(el.getAttribute("data-remove"));for(let x=0;x<targets.length;x++){targets[x].remove()}}})}},false);