$(document).ready(function(){$("body").on("click","[data-step]",function(){$(this).parent().children().removeClass("on");$(this).addClass("on");let el=$(this).data("step");$(el).parent().children().addClass("dn");$(el).removeClass()}).on("click",".steps [data-next]",function(){$($(this).parents(".steps")).find("[data-step].on").next().click()}).on("click",".steps [data-prev]",function(){$($(this).parents(".steps")).find("[data-step].on").prev().click()})});function goto_step(b,a){$(b).find(".step:nth-child("+a+")").click()}function next_step(a){$(a).find(".on").next().click()}function prev_step(a){$(a).find(".on").prev().click()};