function render_cart(){post($("[data-cart]").data("cart"),[],"","","","","render_cart_items")}function render_cart_items(a){let template=$("[data-cart-item-template]").html();let total=0;let quantity=0;if(a.length>0){$("[data-cart-items]").removeClass("dn").html("");$("[data-empty-cart]").addClass("dn");$(a).each(function(b,c){console.log(c);let item=template.replaceAll("{{title}}",c.prod_title).replaceAll("{{params}}",c.prod_params!==undefined?"":c.prod_params).replaceAll("{{quantity}}",1).replaceAll("{{price}}",c.prod_price).replaceAll("{{url}}",c.prod_url).replaceAll("{{image}}",c.prod_image);$("[data-cart-items]").append(item);quantity++;total+=parseFloat(c.prod_price)})}else{$("[data-cart-items]").addClass("dn");$("[data-empty-cart]").removeClass("dn")}$("[data-cart-total]").html(total);$("[data-cart-quantity],[data-cart-count]").html(quantity)};